mapper.insert:
insert into tb_room_type_init (init_id, type_id, type_name,
  num_rooms, num_retain_rooms, price, 
  sale_price, room_date, comment, 
  c_time, u_time, del_status
  )
values (#{initId,jdbcType=INTEGER}, #{typeId,jdbcType=INTEGER}, #{typeName,jdbcType=VARCHAR}, 
  #{numRooms,jdbcType=INTEGER}, #{numRetainRooms,jdbcType=INTEGER}, #{price,jdbcType=DOUBLE}, 
  #{salePrice,jdbcType=DOUBLE}, #{roomDate,jdbcType=TIMESTAMP}, #{comment,jdbcType=VARCHAR}, 
  #{cTime,jdbcType=TIMESTAMP}, #{uTime,jdbcType=TIMESTAMP}, #{delStatus,jdbcType=INTEGER}
  )

mapper.select:
select distinct ti.type_id typeId, trt.type_name typeName, max(ti.num_rooms) numRooms, min(ti.num_retain_rooms) numRetainRooms, avg(ti.price) price,avg(ti.sale_price) salePrice, ti.comment comment,trt.bad_type badType,trt.breakfast breakfast,trt.network network, trt.floor floor,trt.type_note typeNote, trt.price_note priceNote, round(avg(ti.price_1),0) price1, round(avg(ti.price_2),0) price2,round(avg(ti.sale_price_1),0) salePrice1, round(avg(ti.sale_price_2),0) salePrice2, min(ti.per_status) perStatus from tb_room_type_init ti left join tb_room_type trt on ti.type_id = trt.type_id where ti.del_status = 1 and trt.del_status = 1 and ti.room_date BETWEEN #{startDate} and #{endDate}
and ti.type_id = #{typeId}
and trt.env = #{env} group by ti.type_id ,ti.type_name

select ti.init_id initId, ti.type_id typeId, trt.type_name typeName, ti.num_rooms numRooms, ti.num_retain_rooms numRetainRooms, ti.price price,ti.sale_price salePrice, ti.comment comment,trt.bad_type badType,trt.breakfast breakfast,trt.network network, trt.floor floor,trt.type_note typeNote, trt.price_note priceNote, ti.price_1 price1, trt.price_2 price2,trt.sale_price_1 salePrice1, trt.sale_price_2 salePrice2, ti.per_status perStatus from tb_room_type_init ti left join tb_room_type trt on ti.type_id = trt.type_id where ti.del_status = 1 and trt.del_status = 1 and ti.room_date BETWEEN #{startDate} and #{endDate}
and ti.type_id = #{typeId}
and trt.env = #{env}

select count(ti.init_id)
from tb_room_type_init ti
left join tb_room_type trt on ti.type_id = trt.type_id
where ti.del_status = 1
and trt.env = 'cn'

    and ti.type_id = #{typeId}


    and ti.room_date >= #{orderStartDate}


    and ti.room_date <= #{orderEndDate}


    and ti.per_status = #{perStatus}

select ti.init_id initId, ti.type_id typeId, trt.type_name typeName, ti.num_rooms numRooms, ti.num_retain_rooms numRetainRooms, ti.price price,ti.sale_price salePrice, ti.comment comment,trt.bad_type badType,trt.breakfast breakfast,trt.network network, trt.floor floor,trt.type_note typeNote, trt.price_note priceNote, ti.price_1 price1, ti.price_2 price2,ti.sale_price_1 salePrice1, ti.sale_price_2 salePrice2, ti.per_status perStatus, ti.room_date roomDate from tb_room_type_init ti left join tb_room_type trt on ti.type_id = trt.type_id where ti.del_status = 1 and trt.env = 'cn'
and ti.type_id = #{typeId}
and ti.room_date >= #{orderStartDate}
and ti.room_date <= #{orderEndDate}
and ti.per_status = #{perStatus}
order by init_id desc limit #{start} , #{limit}

mapper.update:
update tb_room_type_init set per_status = #{status} where del_status = 1
and init_id in

    #{id}
